mapper.delete:

delete from offline_message
where messag_id = #{messagId,jdbcType=INTEGER}
  and to_id = #{toId,jdbcType=INTEGER}
  and from_user_id = #{fromUserId,jdbcType=INTEGER}

mapper.update:

update offline_message
set text_type = #{textType,jdbcType=VARCHAR},
  text_content = #{textContent,jdbcType=VARCHAR},
  message_type = #{messageType,jdbcType=VARCHAR},
  date = #{date,jdbcType=TIMESTAMP}
where messag_id = #{messagId,jdbcType=INTEGER}
  and to_id = #{toId,jdbcType=INTEGER}
  and from_user_id = #{fromUserId,jdbcType=INTEGER}

mapper.select:

select messag_id, to_id, from_user_id, text_type, text_content, message_type, date
from offline_message
where messag_id = #{messagId,jdbcType=INTEGER}
  and to_id = #{toId,jdbcType=INTEGER}
  and from_user_id = #{fromUserId,jdbcType=INTEGER}

select count( messag_id )
from offline_message
 where  to_id = #{toId,jdbcType=INTEGER}
  and from_user_id = #{fromUserId,jdbcType=INTEGER}
  and message_type = #{messageType,jdbcType=VARCHAR}
